mapper.select:
select t.flow_id as "flowId", ft.flow_template_name as "name", t.f_pdkey as "key", t.version_code as "version" from flow_detail t, flow_template ft where 1 = 1 and t.flow_id = ft.flow_id
and f_pdkey=#{fPdKey,jdbcType=VARCHAR}
order by t.flow_id asc

select t.tache_id as "tacheId", tache_name as "tacheName", t.activity_name as "activityName", t.tache_spec_cd as "tacheSpecCd", t.tache_type_cd as "tacheTypeCd" from tache_detail t where 1 = 1
and flow_id=#{flowId,jdbcType=VARCHAR}
order by t.tache_id asc

select t.transition_id as "transitionId", t.trans_name as "transName", t.transition_type as "transitionType" from transition_detail t where 1 = 1
and flow_id=#{flowId,jdbcType=VARCHAR}
order by t.transition_id asc

mapper.delete:
BEGIN
DELETE from tache_detail where flow_id= #{flowId,jdbcType=DECIMAL};
DELETE from transition_detail where flow_id= #{flowId,jdbcType=DECIMAL};
UPDATE flow_template set flow_template_name=#{name,jdbcType=VARCHAR},description=#{name,jdbcType=VARCHAR} where flow_id= #{flowId,jdbcType=DECIMAL};
END ;

mapper.select:
select i.name as "staffName", i.staff_id as "staffId", t.flow_operator as "id", is_default as "isDefault"
  from flow_operator t, staff_info i
 where i.status_cd='1000' and t.operator_id = i.staff_id AND t.tache_id=#{tacheId,jdbcType=DECIMAL}
